import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  Paper, 
  Avatar, 
  Chip,
  IconButton,
  Skeleton,
  Button,
  AppBar,
  Toolbar,
  Badge,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Divider,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Alert,
  Grid,
  Tooltip,
  Slide,
  Zoom,
  Fade,
  Card,
  CardContent,
  Stack,
  Fab
} from '@mui/material';
import { 
  ArrowBack,
  Groups,
  Mic,
  MicOff,
  PanTool,
  RecordVoiceOver,
  VideoCall,
  Settings,
  ExitToApp,
  VolumeUp,
  VolumeOff,
  PersonAdd,
  Share,
  MoreVert,
  QueueMusic,
  Chat,
  MusicNote,
  Palette,
  Visibility,
  Favorite,
  FavoriteBorder,
  Star,
  StarBorder,
  AutoAwesome,
  Celebration,
  Whatshot,
  TrendingUp,
  EmojiEvents,
  Camera,
  Headphones,
  GraphicEq,
  Equalizer,
  Add,
  Refresh
} from '@mui/icons-material';
import { useParams, useNavigate } from 'react-router-dom';
import { roomsAPI, tracksAPI } from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import { useSocket } from '../contexts/SocketContext_enhanced';
// Import enhanced components
import SynchronizedAudioPlayer from '../components/SynchronizedAudioPlayer';
import ParticipantsListSimple from '../components/ParticipantsListSimple';
import RoomChat from '../components/RoomChat';
import RoomSettings from '../components/RoomSettings';
import RoomThemes from '../components/RoomThemes';

const ModernRoom = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { isAuthenticated, user } = useAuth();
  const { 
    joinRoom, 
    leaveRoom, 
    participants: socketParticipants, 
    roomState, 
    isConnected,
    updateStatus,
    notifyTrackAdded,
    getParticipants,
    socket
  } = useSocket();

  // Room state
  const [room, setRoom] = useState(null);
  const [loading, setLoading] = useState(true);
  const [participants, setParticipants] = useState([]);
  const [roomTracks, setRoomTracks] = useState([]);
  const [userTracks, setUserTracks] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(70);
  const [roomEnergy, setRoomEnergy] = useState(50);

  // Derived state
  const isHost = room?.host_id === user?.id;

  // UI state
  const [showSettings, setShowSettings] = useState(false);
  const [showChat, setShowChat] = useState(false);
  const [showParticipants, setShowParticipants] = useState(false);
  const [showThemes, setShowThemes] = useState(false);
  const [showAddTrack, setShowAddTrack] = useState(false);
  const [sparkles, setSparkles] = useState([]);
  const [roomVibe, setRoomVibe] = useState('chill');

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    loadRoom();
  }, [id, isAuthenticated, navigate]);

  useEffect(() => {
    if (room && user) {
      joinRoom(room.id, user);
    }

    return () => {
      if (room) {
        leaveRoom(room.id);
      }
    };
  }, [room, user, joinRoom, leaveRoom]);

  useEffect(() => {
    if (socketParticipants) {
      console.log('🧑‍🤝‍🧑 ModernRoom: Participants updated:', socketParticipants);
      setParticipants(socketParticipants);
    }
  }, [socketParticipants]);

  useEffect(() => {
    if (roomState) {
      setCurrentTrack(roomState.currentTrack);
      setIsPlaying(roomState.isPlaying);
    }
  }, [roomState]);

  // Set room theme when room data loads
  useEffect(() => {
    if (room && room.background_theme) {
      console.log('🎨 Setting room theme from database:', room.background_theme);
      setRoomVibe(room.background_theme);
    }
  }, [room]);

  // Generate sparkles effect
  useEffect(() => {
    const interval = setInterval(() => {
      if (isPlaying && roomEnergy > 60) {
        const newSparkle = {
          id: Date.now(),
          x: Math.random() * 100,
          y: Math.random() * 100,
          size: Math.random() * 20 + 10,
          color: ['#4ECDC4', '#FFD700', '#FF6B6B', '#9B59B6'][Math.floor(Math.random() * 4)]
        };
        setSparkles(prev => [...prev.slice(-10), newSparkle]);

        setTimeout(() => {
          setSparkles(prev => prev.filter(s => s.id !== newSparkle.id));
        }, 2000);
      }
    }, 500);

    return () => clearInterval(interval);
  }, [isPlaying, roomEnergy]);

  const loadRoom = async () => {
    try {
      setLoading(true);
      console.log('🔍 Loading room with ID:', id);
      const response = await roomsAPI.getById(id);
      console.log('✅ Room API response:', response);
      setRoom(response.data.room); // Note: server returns { room: { ... } }
      await loadRoomTracks();
    } catch (error) {
      console.error('❌ Failed to load room:', error);
      console.error('❌ Error details:', error.response?.data || error.message);
    } finally {
      setLoading(false);
    }
  };

  const loadRoomTracks = async () => {
    try {
      console.log('🎵 Loading tracks for room ID:', id);
      const response = await roomsAPI.getTracks(id);
      console.log('✅ Room tracks API response:', response);
      setRoomTracks(response.data.tracks || []);
    } catch (error) {
      console.error('❌ Failed to load room tracks:', error);
      console.error('❌ Error details:', error.response?.data || error.message);
      setRoomTracks([]);
    }
  };

  const loadUserTracks = async () => {
    try {
      const response = await tracksAPI.getUserTracks();
      setUserTracks(response.data.tracks || []);
    } catch (error) {
      console.error('Failed to load user tracks:', error);
      setUserTracks([]);
    }
  };

  const handleAddTrack = async (track) => {
    try {
      await roomsAPI.addTrack(id, track.id);
      if (notifyTrackAdded) {
        notifyTrackAdded(track);
      }
      await loadRoomTracks();

      // Create energy burst effect
      setRoomEnergy(prev => Math.min(100, prev + 15));
      setTimeout(() => setRoomEnergy(prev => Math.max(0, prev - 5)), 2000);
    } catch (error) {
      console.error('Failed to add track:', error);
    }
  };

  const getVibeGradient = () => {
    const vibes = {
      chill: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      party: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
      focus: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)',
      romantic: 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)',
      energetic: 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)',
      ocean: 'linear-gradient(135deg, #0c4a6e 0%, #38bdf8 100%)',
      forest: 'linear-gradient(135deg, #064e3b 0%, #10b981 100%)',
      cosmic: 'linear-gradient(135deg, #1e1b4b 0%, #6366f1 100%)'
    };
    return vibes[roomVibe] || vibes.chill;
  };

  if (loading) {
    return (
      <Box sx={{ 
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <Box sx={{ textAlign: 'center', color: 'white' }}>
          <Box sx={{ 
            width: 80, 
            height: 80, 
            border: '4px solid rgba(255,255,255,0.3)',
            borderTop: '4px solid white',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            mx: 'auto',
            mb: 2
          }} />
          <Typography variant="h6">Loading the vibe...</Typography>
        </Box>
      </Box>
    );
  }

  if (!room) {
    return (
      <Container maxWidth="md" sx={{ mt: 4 }}>
        <Alert severity="error">Room not found</Alert>
      </Container>
    );
  }

  return (
    <Box sx={{ 
      minHeight: '100vh',
      background: getVibeGradient(),
      position: 'relative',
      overflow: 'hidden',
      '& @keyframes beat': {
        '0%, 100%': { transform: 'scale(1)' },
        '50%': { transform: 'scale(1.1)' }
      },
      '& @keyframes pulse': {
        '0%, 100%': { opacity: 1 },
        '50%': { opacity: 0.7 }
      },
      '& @keyframes floating': {
        '0%, 100%': { transform: 'translateY(0px)' },
        '50%': { transform: 'translateY(-20px)' }
      },
      '& @keyframes sparkle': {
        '0%': { transform: 'scale(0) rotate(0deg)', opacity: 0 },
        '50%': { transform: 'scale(1) rotate(180deg)', opacity: 1 },
        '100%': { transform: 'scale(0) rotate(360deg)', opacity: 0 }
      },
      '& @keyframes spin': {
        '0%': { transform: 'rotate(0deg)' },
        '100%': { transform: 'rotate(360deg)' }
      },
      '& @keyframes bounce': {
        '0%, 20%, 50%, 80%, 100%': { transform: 'translateY(0)' },
        '40%': { transform: 'translateY(-10px)' },
        '60%': { transform: 'translateY(-5px)' }
      },
      '& @keyframes energyBurst': {
        '0%, 100%': { boxShadow: '0 0 5px rgba(255,107,107,0.5)' },
        '50%': { boxShadow: '0 0 20px rgba(255,107,107,0.8)' }
      }
    }}>
      {/* Animated Background Elements */}
      <Box sx={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        pointerEvents: 'none',
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'radial-gradient(circle at 20% 50%, rgba(120, 119, 198, 0.3) 0%, transparent 50%), radial-gradient(circle at 80% 20%, rgba(255, 119, 198, 0.3) 0%, transparent 50%), radial-gradient(circle at 40% 80%, rgba(120, 219, 255, 0.3) 0%, transparent 50%)',
          animation: 'floating 6s ease-in-out infinite'
        }
      }} />

      {/* Sparkles Effect */}
      {sparkles.map(sparkle => (
        <Box
          key={sparkle.id}
          sx={{
            position: 'absolute',
            left: `${sparkle.x}%`,
            top: `${sparkle.y}%`,
            width: sparkle.size,
            height: sparkle.size,
            background: sparkle.color,
            borderRadius: '50%',
            animation: 'sparkle 2s ease-in-out',
            pointerEvents: 'none',
            zIndex: 1
          }}
        />
      ))}

      {/* Header */}
      <AppBar 
        position="sticky" 
        sx={{ 
          background: 'rgba(255, 255, 255, 0.1)',
          backdropFilter: 'blur(20px)',
          borderBottom: '1px solid rgba(255, 255, 255, 0.2)'
        }}
      >
        <Toolbar>
          <IconButton 
            edge="start" 
            color="inherit" 
            onClick={() => navigate('/rooms')}
            sx={{ 
              mr: 2,
              '&:hover': { transform: 'scale(1.1)' }
            }}
          >
            <ArrowBack />
          </IconButton>

          <Avatar sx={{ 
            width: 40, 
            height: 40, 
            mr: 2,
            background: getVibeGradient(),
            animation: isPlaying ? 'beat 1s infinite' : 'none'
          }}>
            <MusicNote />
          </Avatar>

          <Box sx={{ flexGrow: 1 }}>
            <Typography variant="h6" sx={{ fontWeight: 'bold' }}>
              {room.title || room.name}
            </Typography>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <Chip 
                icon={<Groups />}
                label={`${participants.length} vibing`}
                size="small"
                sx={{ 
                  background: 'rgba(255, 255, 255, 0.2)',
                  color: 'white'
                }}
              />
              <Chip 
                label={roomVibe}
                size="small"
                sx={{ 
                  background: 'rgba(78, 205, 196, 0.8)',
                  color: 'white',
                  textTransform: 'capitalize'
                }}
              />
            </Box>
          </Box>

          {/* Energy Meter */}
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mr: 2 }}>
            <Typography variant="caption" sx={{ color: 'rgba(255,255,255,0.8)' }}>
              Energy
            </Typography>
            <Box sx={{ 
              width: 60, 
              height: 8, 
              background: 'rgba(255,255,255,0.2)', 
              borderRadius: 4,
              overflow: 'hidden'
            }}>
              <Box
                sx={{
                  width: `${roomEnergy}%`,
                  height: '100%',
                  background: roomEnergy > 80 ? 'linear-gradient(45deg, #FF6B6B, #FFD93D)' :
                             roomEnergy > 50 ? 'linear-gradient(45deg, #4ECDC4, #44A08D)' :
                             'linear-gradient(45deg, #667eea, #764ba2)',
                  transition: 'all 0.5s ease',
                  animation: roomEnergy > 80 ? 'energyBurst 1s infinite' : 'none'
                }}
              />
            </Box>
          </Box>

          {/* Action Buttons */}
          <Box sx={{ display: 'flex', gap: 1 }}>
            <Tooltip title="Participants">
              <IconButton 
                color="inherit"
                onClick={() => {
                  setShowParticipants(true);
                  getParticipants && getParticipants();
                }}
                sx={{ '&:hover': { transform: 'scale(1.1)' } }}
              >
                <Badge badgeContent={participants.length} color="secondary">
                  <Groups />
                </Badge>
              </IconButton>
            </Tooltip>

            <Tooltip title="Chat">
              <IconButton 
                color="inherit"
                onClick={() => setShowChat(true)}
                sx={{ '&:hover': { transform: 'scale(1.1)' } }}
              >
                <Chat />
              </IconButton>
            </Tooltip>

            <Tooltip title="Themes">
              <IconButton 
                color="inherit"
                onClick={() => setShowThemes(true)}
                sx={{ '&:hover': { transform: 'scale(1.1)' } }}
              >
                <Palette />
              </IconButton>
            </Tooltip>

            <Tooltip title="Settings">
              <IconButton 
                color="inherit"
                onClick={() => setShowSettings(true)}
                sx={{ '&:hover': { transform: 'scale(1.1)' } }}
              >
                <Settings />
              </IconButton>
            </Tooltip>
          </Box>
        </Toolbar>
      </AppBar>

      {/* Main Content */}
      <Container maxWidth="xl" sx={{ py: 3, position: 'relative', zIndex: 2 }}>
        <Grid container spacing={3} sx={{ minHeight: 'calc(100vh - 200px)' }}>
          {/* Audio Player Section */}
          <Grid item xs={12} lg={8}>
            <Zoom in timeout={500}>
              <Box>
                <SynchronizedAudioPlayer
                  roomId={room.id}
                  isHost={room.host_id === user?.id}
                  tracks={roomTracks}
                />
              </Box>
            </Zoom>
          </Grid>

          {/* Track Queue Section */}
          <Grid item xs={12} lg={4}>
            <Fade in timeout={700}>
              <Card sx={{
                background: 'linear-gradient(135deg, rgba(255, 255, 255, 0.1) 0%, rgba(255, 255, 255, 0.05) 100%)',
                backdropFilter: 'blur(20px)',
                borderRadius: '24px',
                border: '1px solid rgba(255, 255, 255, 0.2)',
                color: 'white',
                height: 'fit-content',
                position: 'relative',
                overflow: 'hidden',
                '&::before': {
                  content: '""',
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  right: 0,
                  bottom: 0,
                  background: roomTracks.length > 0 ? 
                    'linear-gradient(45deg, rgba(78, 205, 196, 0.05), rgba(68, 160, 141, 0.05))' :
                    'transparent',
                  zIndex: -1
                }
              }}>
                <CardContent>
                  <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 3 }}>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Box sx={{
                        width: 32,
                        height: 32,
                        borderRadius: '50%',
                        background: 'linear-gradient(45deg, #4ECDC4, #44A08D)',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center'
                      }}>
                        <QueueMusic sx={{ color: 'white', fontSize: 18 }} />
                      </Box>
                      <Box>
                        <Typography variant="h6" sx={{ fontWeight: 'bold', lineHeight: 1 }}>
                          Queue
                        </Typography>
                        <Typography variant="caption" sx={{ color: 'rgba(255, 255, 255, 0.7)' }}>
                          {roomTracks.length} {roomTracks.length === 1 ? 'track' : 'tracks'}
                        </Typography>
                      </Box>
                    </Box>
                    <Button
                      variant="contained"
                      size="small"
                      startIcon={<Add />}
                      onClick={() => {
                        setShowAddTrack(true);
                        loadUserTracks();
                      }}
                      sx={{
                        background: 'linear-gradient(45deg, #4ECDC4, #44A08D)',
                        borderRadius: '20px',
                        textTransform: 'none',
                        fontWeight: 'bold',
                        px: 2,
                        '&:hover': {
                          background: 'linear-gradient(45deg, #44A08D, #4ECDC4)',
                          transform: 'translateY(-2px)',
                          boxShadow: '0 8px 16px rgba(78, 205, 196, 0.3)'
                        },
                        transition: 'all 0.3s ease'
                      }}
                    >
                      Add Track
                    </Button>
                  </Box>

                  {roomTracks.length === 0 ? (
                    <Box sx={{ textAlign: 'center', py: 4, opacity: 0.7 }}>
                      <QueueMusic sx={{ fontSize: 48, mb: 1 }} />
                      <Typography variant="body2">
                        No tracks in queue
                      </Typography>
                      <Typography variant="caption">
                        Add some tracks to get the party started!
                      </Typography>
                    </Box>
                  ) : (
                    <List>
                      {roomTracks.map((track, index) => {
                        const isCurrentlyPlaying = currentTrack && currentTrack.id === track.id;

                        return (
                          <ListItem 
                            key={track.id}
                            button
                            onClick={() => {
                              console.log('🎵 Track clicked:', track.title, 'isHost:', isHost, 'socket:', !!socket, 'room:', !!room);
                              if (isHost && socket && room) {
                                console.log('🚀 Emitting room:play event for track:', track.id);
                                // Host can select any track to play
                                socket.emit('room:play', {
                                  roomId: room.id,
                                  trackId: track.id,
                                  position: 0
                                });
                              } else {
                                console.log('❌ Cannot play track - isHost:', isHost, 'socket:', !!socket, 'room:', !!room);
                              }
                            }}
                            sx={{
                              borderRadius: '16px',
                              mb: 1,
                              background: isCurrentlyPlaying ? 
                                'linear-gradient(135deg, rgba(78, 205, 196, 0.15) 0%, rgba(68, 160, 141, 0.1) 100%)' : 
                                'rgba(255, 255, 255, 0.05)',
                              border: isCurrentlyPlaying ? 
                                '2px solid rgba(78, 205, 196, 0.4)' : 
                                '2px solid transparent',
                              backdropFilter: 'blur(10px)',
                              '&:hover': {
                                background: isCurrentlyPlaying ?
                                  'linear-gradient(135deg, rgba(78, 205, 196, 0.2) 0%, rgba(68, 160, 141, 0.15) 100%)' :
                                  'rgba(255, 255, 255, 0.1)',
                                cursor: isHost ? 'pointer' : 'default',
                                transform: isHost ? 'translateY(-2px)' : 'none',
                                boxShadow: isHost ? '0 8px 16px rgba(0,0,0,0.2)' : 'none'
                              },
                              transition: 'all 0.3s ease',
                              position: 'relative',
                              overflow: 'hidden'
                            }}
                          >
                            <ListItemAvatar>
                              <Avatar sx={{
                                background: isCurrentlyPlaying ? 
                                  'linear-gradient(45deg, #4ECDC4, #44A08D)' : 
                                  'rgba(255, 255, 255, 0.15)',
                                animation: isCurrentlyPlaying && isPlaying ? 'beat 1s infinite' : 'none',
                                border: isCurrentlyPlaying ? '2px solid rgba(78, 205, 196, 0.3)' : 'none',
                                width: 48,
                                height: 48,
                                fontSize: isCurrentlyPlaying ? 20 : 16,
                                fontWeight: 'bold'
                              }}>
                                {isCurrentlyPlaying ? <Equalizer /> : index + 1}
                              </Avatar>
                            </ListItemAvatar>
                            <ListItemText
                              primary={
                                <Typography variant="body1" sx={{ 
                                  fontWeight: isCurrentlyPlaying ? 'bold' : 'normal',
                                  color: 'white'
                                }}>
                                  {track.title}
                                </Typography>
                              }
                              secondary={
                                <Box sx={{ color: 'rgba(255, 255, 255, 0.7)' }}>
                                  <Typography variant="caption" display="block">
                                    {track.artist}
                                  </Typography>
                                  <Typography variant="caption" sx={{ 
                                    background: 'rgba(255, 255, 255, 0.1)',
                                    px: 1,
                                    py: 0.25,
                                    borderRadius: 1,
                                    display: 'inline-block',
                                    mt: 0.5
                                  }}>
                                    Added by {track.added_by_username}
                                  </Typography>
                                </Box>
                              }
                            />
                            {isCurrentlyPlaying && (
                              <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-end', gap: 1 }}>
                                <Chip
                                  label={isPlaying ? "Now Playing" : "Paused"}
                                  size="small"
                                  sx={{
                                    background: isPlaying ? 
                                      'linear-gradient(45deg, #4ECDC4, #44A08D)' : 
                                      'linear-gradient(45deg, #666, #888)',
                                    color: 'white',
                                    fontWeight: 'bold',
                                    animation: isPlaying ? 'pulse 2s infinite' : 'none',
                                    boxShadow: '0 4px 8px rgba(0,0,0,0.3)'
                                  }}
                                />
                                {isPlaying && (
                                  <Box sx={{
                                    width: 24,
                                    height: 24,
                                    borderRadius: '50%',
                                    background: 'linear-gradient(45deg, #4ECDC4, #44A08D)',
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    animation: 'beat 1s infinite'
                                  }}>
                                    <Box sx={{
                                      width: 8,
                                      height: 8,
                                      borderRadius: '50%',
                                      background: 'white'
                                    }} />
                                  </Box>
                                )}
                              </Box>
                            )}
                          </ListItem>
                        );
                      })}
                    </List>
                  )}
                </CardContent>
              </Card>
            </Fade>
          </Grid>
        </Grid>
      </Container>

      {/* Floating Action Buttons */}
      <Fab
        color="primary"
        sx={{
          position: 'fixed',
          bottom: 32,
          right: 32,
          background: 'linear-gradient(45deg, #4ECDC4, #44A08D)',
          '&:hover': {
            background: 'linear-gradient(45deg, #44A08D, #4ECDC4)',
            transform: 'scale(1.1)'
          },
          animation: roomEnergy > 70 ? 'bounce 2s infinite' : 'none'
        }}
        onClick={() => {
          setShowParticipants(true);
          getParticipants && getParticipants();
        }}
      >
        <Badge badgeContent={participants.length} color="secondary">
          <Groups />
        </Badge>
      </Fab>

      {/* Modals */}
      {/* Add Track Modal */}
      <Dialog
        open={showAddTrack}
        onClose={() => setShowAddTrack(false)}
        maxWidth="md"
        fullWidth
        PaperProps={{
          sx: {
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(20px)',
            borderRadius: '24px',
            border: '1px solid rgba(255, 255, 255, 0.2)'
          }
        }}
      >
        <DialogTitle sx={{ color: 'white' }}>
          Add Track to Queue
        </DialogTitle>
        <DialogContent>
          {userTracks.length === 0 ? (
            <Box sx={{ textAlign: 'center', py: 4, color: 'white' }}>
              <MusicNote sx={{ fontSize: 48, mb: 1, opacity: 0.5 }} />
              <Typography variant="body1" sx={{ mb: 1 }}>
                No tracks available
              </Typography>
              <Typography variant="body2" sx={{ opacity: 0.7 }}>
                Upload some tracks first to add them to the room queue
              </Typography>
            </Box>
          ) : (
            <List>
              {userTracks.map((track) => (
                <ListItem 
                  key={track.id}
                  sx={{
                    borderRadius: '12px',
                    mb: 1,
                    background: 'rgba(255, 255, 255, 0.05)',
                    '&:hover': {
                      background: 'rgba(255, 255, 255, 0.1)'
                    }
                  }}
                >
                  <ListItemAvatar>
                    <Avatar sx={{ background: 'rgba(255, 255, 255, 0.2)' }}>
                      <MusicNote />
                    </Avatar>
                  </ListItemAvatar>
                  <ListItemText
                    primary={track.title}
                    secondary={
                      <Typography variant="caption" sx={{ color: 'rgba(255, 255, 255, 0.7)' }}>
                        {track.artist} • {Math.round(track.duration / 60)}:{String(Math.round(track.duration % 60)).padStart(2, '0')}
                      </Typography>
                    }
                    sx={{ color: 'white' }}
                  />
                  <Button
                    variant="contained"
                    size="small"
                    onClick={() => handleAddTrack(track)}
                    sx={{
                      background: 'linear-gradient(45deg, #4ECDC4, #44A08D)',
                      '&:hover': {
                        background: 'linear-gradient(45deg, #44A08D, #4ECDC4)',
                      }
                    }}
                  >
                    Add
                  </Button>
                </ListItem>
              ))}
            </List>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowAddTrack(false)} sx={{ color: 'white' }}>
            Close
          </Button>
        </DialogActions>
      </Dialog>

      {/* Participants Modal */}
      <Dialog
        open={showParticipants}
        onClose={() => setShowParticipants(false)}
        maxWidth="sm"
        fullWidth
        PaperProps={{
          sx: {
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(20px)',
            borderRadius: '24px',
            border: '1px solid rgba(255, 255, 255, 0.2)'
          }
        }}
      >
        <DialogTitle sx={{ color: 'white', textAlign: 'center', display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          Who's in the Vibe
          <IconButton 
            onClick={() => {
              console.log('🔄 Refreshing participants list...');
              getParticipants && getParticipants();
            }}
            sx={{ color: 'white' }}
          >
            <Tooltip title="Refresh Participants">
              <Refresh />
            </Tooltip>
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <ParticipantsListSimple 
            participants={participants}
            isHost={room?.host_id === user?.id}
            currentUser={user}
          />
        </DialogContent>
      </Dialog>

      {/* Chat Modal */}
      <Dialog
        open={showChat}
        onClose={() => setShowChat(false)}
        maxWidth="md"
        fullWidth
        PaperProps={{
          sx: {
            height: '80vh',
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(20px)',
            borderRadius: '24px',
            border: '1px solid rgba(255, 255, 255, 0.2)'
          }
        }}
      >
        <DialogTitle sx={{ color: 'white' }}>
          Room Chat
        </DialogTitle>
        <DialogContent>
          <RoomChat room={room} />
        </DialogContent>
      </Dialog>

      {/* Settings Modal */}
      <RoomSettings 
        open={showSettings}
        onClose={() => setShowSettings(false)}
        room={room} 
        isHost={room?.host_id === user?.id}
        onRoomUpdated={setRoom}
        onRoomDeleted={() => {
          setShowSettings(false);
          navigate('/rooms');
        }}
      />

      {/* Themes Modal */}
      <Dialog
        open={showThemes}
        onClose={() => setShowThemes(false)}
        maxWidth="md"
        fullWidth
        PaperProps={{
          sx: {
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(20px)',
            borderRadius: '24px',
            border: '1px solid rgba(255, 255, 255, 0.2)'
          }
        }}
      >
        <DialogTitle sx={{ color: 'white' }}>
          Choose Your Vibe
        </DialogTitle>
        <DialogContent>
          <RoomThemes 
            room={room} 
            onThemeChange={(theme) => {
              setRoom({...room, background_theme: theme});
              setRoomVibe(theme);
            }}
          />
        </DialogContent>
      </Dialog>
    </Box>
  );
};

export default ModernRoom;
