import React, { useState } from 'react';
import { Box, Grid, Card, CardContent, Typography, Button } from '@mui/material';

const mockThemes = [
	{ key: 'light', name: 'Light', description: 'Bright and clean', color: '#fff' },
	{ key: 'dark', name: 'Dark', description: 'Sleek and modern', color: '#222' },
	{ key: 'aqua', name: 'Aqua', description: 'Cool blue vibes', color: '#4ecdc4' },
];

const RoomThemes = () => {
	const [selected, setSelected] = useState('light');

	const handleSelect = (key) => {
		setSelected(key);
	};

	return (
		<Box sx={{ p: 3 }}>
			<Typography variant="h4" sx={{ mb: 3, fontWeight: 700 }}>
				Room Themes
			</Typography>
			<Grid container spacing={3}>
				{mockThemes.map((theme) => (
					<Grid item xs={12} sm={6} md={4} key={theme.key}>
						<Card
							sx={{
								background: theme.color,
								color: theme.key === 'dark' ? '#fff' : '#222',
								border: selected === theme.key ? '2px solid #4ecdc4' : '2px solid transparent',
								boxShadow: selected === theme.key ? 6 : 1,
								cursor: 'pointer',
								transition: 'all 0.2s',
							}}
							onClick={() => handleSelect(theme.key)}
						>
							<CardContent>
								<Typography variant="h6" sx={{ fontWeight: 600 }}>
									{theme.name}
								</Typography>
								<Typography variant="body2" sx={{ mb: 2 }}>
									{theme.description}
								</Typography>
								<Button
									variant={selected === theme.key ? 'contained' : 'outlined'}
									color="primary"
									size="small"
									sx={{ mt: 1 }}
									onClick={(e) => {
										e.stopPropagation();
										handleSelect(theme.key);
									}}
								>
									{selected === theme.key ? 'Selected' : 'Select'}
								</Button>
							</CardContent>
						</Card>
					</Grid>
				))}
			</Grid>
		</Box>
	);
};

export default RoomThemes;
