import React, { useState, useEffect } from 'react';
import {
  Box,
  Grid,
  Card,
  CardContent,
  Typography,
  Button,
  Chip,
  IconButton,
  Tooltip,
  Paper,
  alpha,
  Snackbar,
  Alert
} from '@mui/material';
import {
  Palette,
  Check,
  Brightness4,
  Brightness7,
  ColorLens,
  Gradient,
  WbSunny,
  Water,
  Forest,
  AutoAwesome,
  Flare,
  Circle
} from '@mui/icons-material';
import { roomsAPI } from '../services/api';

const themes = {
  chill: {
    name: 'Chill Vibes',
    description: 'Relaxed and peaceful',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    colors: ['#667eea', '#764ba2'],
    icon: <Brightness4 />,
    mood: 'relaxed'
  },
  party: {
    name: 'Party Mode',
    description: 'High energy and fun',
    background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
    colors: ['#f093fb', '#f5576c'],
    icon: <Flare />,
    mood: 'energetic'
  },
  focus: {
    name: 'Focus Flow',
    description: 'Clean and concentrated',
    background: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)',
    colors: ['#4facfe', '#00f2fe'],
    icon: <Water />,
    mood: 'focused'
  },
  romantic: {
    name: 'Romantic',
    description: 'Warm and intimate',
    background: 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)',
    colors: ['#43e97b', '#38f9d7'],
    icon: <AutoAwesome />,
    mood: 'romantic'
  },
  energetic: {
    name: 'High Energy',
    description: 'Vibrant and dynamic',
    background: 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)',
    colors: ['#fa709a', '#fee140'],
    icon: <WbSunny />,
    mood: 'dynamic'
  },
  ocean: {
    name: 'Ocean Depths',
    description: 'Deep and serene',
    background: 'linear-gradient(135deg, #0c4a6e 0%, #38bdf8 100%)',
    colors: ['#0c4a6e', '#38bdf8'],
    icon: <Water />,
    mood: 'serene'
  },
  forest: {
    name: 'Forest Green',
    description: 'Natural and grounding',
    background: 'linear-gradient(135deg, #064e3b 0%, #10b981 100%)',
    colors: ['#064e3b', '#10b981'],
    icon: <Forest />,
    mood: 'natural'
  },
  cosmic: {
    name: 'Cosmic Purple',
    description: 'Mystical and dreamy',
    background: 'linear-gradient(135deg, #1e1b4b 0%, #6366f1 100%)',
    colors: ['#1e1b4b', '#6366f1'],
    icon: <AutoAwesome />,
    mood: 'mystical'
  }
};

const RoomThemes = ({ room, onThemeChange }) => {
  const [selectedTheme, setSelectedTheme] = useState(room?.background_theme || 'chill');
  const [previewTheme, setPreviewTheme] = useState(null);
  const [saving, setSaving] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [error, setError] = useState('');

  // Update selected theme when room prop changes
  useEffect(() => {
    if (room?.background_theme) {
      console.log('🎨 RoomThemes: Updating selected theme from room:', room.background_theme);
      setSelectedTheme(room.background_theme);
    }
  }, [room?.background_theme]);

  const handleThemeSelect = async (themeKey) => {
    try {
      setSaving(true);
      setError('');

      // Update room theme on server
      await roomsAPI.updateSettings(room.id, { background_theme: themeKey });

      setSelectedTheme(themeKey);
      setShowSuccess(true);

      if (onThemeChange) {
        onThemeChange(themeKey);
      }

      console.log('✅ Theme updated successfully:', themeKey);
    } catch (error) {
      console.error('❌ Failed to update theme:', error);
      setError('Failed to update theme. Please try again.');
    } finally {
      setSaving(false);
    }
  };

  const handlePreview = (themeKey) => {
    setPreviewTheme(themeKey);
  };

  const clearPreview = () => {
    setPreviewTheme(null);
  };

  return (
    <Box sx={{ p: 2 }}>
      {/* Current Theme Display */}
      <Box sx={{ mb: 4, textAlign: 'center' }}>
        <Typography variant="h6" sx={{ color: 'white', mb: 2 }}>
          Current Theme: {themes[selectedTheme]?.name || 'Chill Vibes'}
        </Typography>
        <Paper
          sx={{
            height: 80,
            background: previewTheme ? themes[previewTheme].background : themes[selectedTheme].background,
            borderRadius: '16px',
            border: '2px solid rgba(255, 255, 255, 0.3)',
            transition: 'all 0.3s ease',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            position: 'relative',
            overflow: 'hidden'
          }}
        >
          <Box sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(10px)'
          }} />
          <Typography variant="h6" sx={{ 
            color: 'white', 
            fontWeight: 'bold',
            position: 'relative',
            zIndex: 1
          }}>
            {previewTheme ? `Preview: ${themes[previewTheme].name}` : themes[selectedTheme].name}
          </Typography>
        </Paper>
      </Box>

      {/* Theme Grid */}
      <Grid container spacing={2}>
        {Object.entries(themes).map(([themeKey, theme]) => (
          <Grid item xs={12} sm={6} md={4} key={themeKey}>
            <Card
              sx={{
                background: 'rgba(255, 255, 255, 0.1)',
                backdropFilter: 'blur(20px)',
                borderRadius: '16px',
                border: selectedTheme === themeKey ? 
                  '2px solid #4ECDC4' : 
                  '2px solid rgba(255, 255, 255, 0.2)',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                overflow: 'hidden',
                position: 'relative',
                '&:hover': {
                  transform: 'translateY(-4px)',
                  boxShadow: '0 12px 24px rgba(0,0,0,0.3)',
                  border: '2px solid rgba(255, 255, 255, 0.4)'
                }
              }}
              onMouseEnter={() => handlePreview(themeKey)}
              onMouseLeave={clearPreview}
              onClick={() => handleThemeSelect(themeKey)}
            >
              {/* Theme Preview */}
              <Box
                sx={{
                  height: 80,
                  background: theme.background,
                  position: 'relative',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center'
                }}
              >
                <Box sx={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  right: 0,
                  bottom: 0,
                  background: 'rgba(0, 0, 0, 0.2)',
                  backdropFilter: 'blur(2px)'
                }} />

                <Box sx={{ 
                  position: 'relative', 
                  zIndex: 1,
                  color: 'white',
                  display: 'flex',
                  alignItems: 'center',
                  gap: 1
                }}>
                  {theme.icon}
                  {selectedTheme === themeKey && (
                    <Check sx={{ 
                      color: '#4ECDC4',
                      background: 'rgba(255, 255, 255, 0.9)',
                      borderRadius: '50%',
                      p: 0.5,
                      fontSize: 20
                    }} />
                  )}
                </Box>

                {/* Color dots */}
                <Box sx={{
                  position: 'absolute',
                  bottom: 8,
                  right: 8,
                  display: 'flex',
                  gap: 0.5
                }}>
                  {theme.colors.map((color, index) => (
                    <Circle
                      key={index}
                      sx={{
                        color: color,
                        fontSize: 12,
                        filter: 'drop-shadow(0 1px 2px rgba(0,0,0,0.5))'
                      }}
                    />
                  ))}
                </Box>
              </Box>

              <CardContent sx={{ p: 2 }}>
                <Typography variant="subtitle1" sx={{ 
                  color: 'white', 
                  fontWeight: 'bold',
                  mb: 0.5
                }}>
                  {theme.name}
                </Typography>
                <Typography variant="caption" sx={{ 
                  color: 'rgba(255, 255, 255, 0.7)',
                  display: 'block',
                  mb: 1
                }}>
                  {theme.description}
                </Typography>
                <Chip
                  label={theme.mood}
                  size="small"
                  sx={{
                    background: alpha(theme.colors[0], 0.3),
                    color: 'white',
                    textTransform: 'capitalize',
                    fontSize: '0.75rem'
                  }}
                />
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Instructions */}
      <Box sx={{ mt: 3, textAlign: 'center' }}>
        <Typography variant="body2" sx={{ 
          color: 'rgba(255, 255, 255, 0.7)',
          mb: 1
        }}>
          💡 Hover to preview • Click to select • {saving ? 'Saving...' : 'Changes apply instantly'}
        </Typography>
        <Typography variant="caption" sx={{ 
          color: 'rgba(255, 255, 255, 0.5)'
        }}>
          Theme changes the room's background gradient and overall vibe
        </Typography>
      </Box>

      {/* Success/Error Notifications */}
      <Snackbar
        open={showSuccess}
        autoHideDuration={3000}
        onClose={() => setShowSuccess(false)}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert 
          onClose={() => setShowSuccess(false)} 
          severity="success"
          sx={{
            background: 'rgba(76, 175, 80, 0.1)',
            color: '#99ff99',
            border: '1px solid rgba(76, 175, 80, 0.3)',
            '& .MuiAlert-icon': { color: '#99ff99' }
          }}
        >
          Theme updated successfully! 🎨
        </Alert>
      </Snackbar>

      <Snackbar
        open={!!error}
        autoHideDuration={5000}
        onClose={() => setError('')}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert 
          onClose={() => setError('')} 
          severity="error"
          sx={{
            background: 'rgba(244, 67, 54, 0.1)',
            color: '#ff9999',
            border: '1px solid rgba(244, 67, 54, 0.3)',
            '& .MuiAlert-icon': { color: '#ff9999' }
          }}
        >
          {error}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default RoomThemes;
